import React, {useEffect, useState } from 'react'
import Head from './Head'
import imageSlider from '../../../data'
import "./header.css"
import { Link } from 'react-router-dom'
function Header() {
    const [click, setClick] = useState(false)
    const [currentState, setCurrentState] = useState(0);
    useEffect(() => {
        const timer = setTimeout(() => {
            if (currentState === 3) {
                setCurrentState(0)
            } else {
                setCurrentState(currentState + 1)
            }
        }, 5000)
        return () => clearTimeout(timer)
    }, [currentState])
    const bgImageStyle = {
        backgroundImage: `url(${imageSlider[currentState].URL})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        height: '100%'
    }
    const gotoNext = (currentState) => {
        setCurrentState(currentState)
    }
    return (
        <>
            <Head />
            <header>
                <nav className='flexSB'>
                    <ul className={click ? 'mobile-nav' : 'flexSB'} onClick={() => setClick(false)}>
                        <li><Link to={"/"}>Accueil</Link></li>
                        <li><Link to={"/about"}>Environ</Link></li>
                        <li><Link to={"/services"}>Services</Link></li>
                        <li><Link to={"/register"}>Register</Link></li>
                        <li><Link to={"/login"}>Connectez-vous</Link></li>
                    </ul>
                    <div className='start'>
                        <div className="button">OBTENIR DES SERVICES</div>
                    </div>
                    <button className='toggle' onClick={() => setClick(!click)} >
                        {click ? <i className='fa fa-times'></i> : <i className='fa fa-bars'></i>}
                    </button>
                </nav>
            </header>
            <div style={bgImageStyle}></div>
            <div className="transparent-background"></div>
            <div className="description">
                <div className="carousel-boullt">
                    {
                        imageSlider.map((imageSlider, currentState) => (
                            <span key={imageSlider} onClick={() => gotoNext(currentState)}></span>
                        ))
                    }
                </div>
            </div>
        </>
    )
}

export default Header
