import React from 'react';
import './commentaire.css';
import Com1 from '../../../src/image/comA.jpg';
import Com2 from '../../../src/image/comB.jpg';
import Com3 from '../../../src/image/comC.jpg';
import {Pagination} from 'swiper/modules';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
const data = [
  {
    id: 1,
    image: Com1,
    title: 'Abdallah',
    subtitle: 'DÉPANNAGE & RÉPARATION',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 2,
    image: Com2,
    title: 'Mohammed Alrami',
    subtitle: 'DÉPANNAGE & RÉPARATION',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 3,
    image: Com3,
    title: 'Fatima Al Zahra',
    subtitle: 'PETITS TRAVAUX',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 4,
    image: Com3,
    title: 'Ryan Fahmi',
    subtitle: 'PETITS TRAVAUX',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 5,
    image: Com3,
    title: 'Mohamed Ali',
    subtitle: 'PETITS TRAVAUX',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 6,
    image: Com3,
    title: 'Mohamed Ali',
    subtitle: 'NETTOYAGE',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 7,
    image: Com3,
    title: 'Ali',
    subtitle: 'NETTOYAGE',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 8,
    image: Com3,
    title: 'SECURITE',
    subtitle: 'NETTOYAGE',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 9,
    image: Com3,
    title: 'SECURITE',
    subtitle: 'NETTOYAGE',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },
  {
    id: 10,
    image: Com3,
    title: 'SECURITE',
    subtitle: 'NETTOYAGE',
    commentaire: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit Magni aut quas tempora facere id reprehenderit',
  },


];

const Commentaire = () => {
  return (
    <div className="testimonials">
    <section >
      <h1 className="section_title">Commentaires Des Clients</h1>
      <Swiper className="testimonials_container grd"
       modules={[Pagination]}
       spaceBetween={30}
       slidesPerView={2}
       loop={true}
       grabCursor={true}
       pagination={{ clickable: true }}
       >
        {data.map(({ id, image, title, subtitle, commentaire }) => {
          return (
            <SwiperSlide className="testimonials_item" key={id}>
              <div className="thumb">
                <img src={image} alt="" className='rounded-pill'/>
              </div>
              <h3 className="testimonials_title">{title}</h3>
              <h5 className="subtitle">{subtitle}</h5>
              <div className="comment">{commentaire}</div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </section>
    </div>
  );
};

export default Commentaire;
