import React, { useState } from 'react';
import { Lock, Person } from '@mui/icons-material';
import { Link, useNavigate } from 'react-router-dom';
import './login.css';
import Header from '../../components/common/heading/Header';
import Footer from '../../components/footer/Footer';
import { useDispatch } from 'react-redux';
import { login } from '../../redux/UserSlice';

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleCheckboxChange = () => {
    setRememberMe(!rememberMe);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validate()) {
      dispatch(login({ email, password, loggedIn: true }));
      // Additional logic for authentication
    }
  };

  const validate = () => {
    if (email.trim() === '') {
      alert('Please enter your email.');
      return false;
    }
    if (password.trim() === '') {
      alert('Please enter your password.');
      return false;
    }
    return true;
  };

  return (
    <div className='loginTopPage'>
      <Header />
      <div className='loginTop'>
        <form className='loginForm' onSubmit={handleSubmit}>
          <h1>Connectez-vous</h1>
          <div className='inputBox'>
            <input type='email' placeholder='Nom d’utilisateur' value={email} onChange={(e) => setEmail(e.target.value)} required />
            <Person className='iconLogin' />
          </div>
          <div className='inputBox'>
            <input type='password' placeholder='Mot de passe' value={password} onChange={(e) => setPassword(e.target.value)} required />
            <Lock className='iconLogin' />
          </div>
          <div className="rememberForgot">
            <label className='labelLogin'>
              <input className='checkbox' type="checkbox" checked={rememberMe} onChange={handleCheckboxChange} />
              Rappelle-moi
            </label>
            <Link to={'/forgotPass'}>Mot de passe oublié?</Link>
          </div>
          <button type='submit'>Login</button>
          <div className='registerLink'>
            <p> Vous n’avez pas de compte? <Link to={'/register'}>Register</Link></p>
          </div>
        </form>
      </div>
      <Footer />
    </div>
  );
}

export default Login;
