import React from 'react';
import "./forgotPass.css";
import { Link } from 'react-router-dom';
import { Email } from '@mui/icons-material';
export default function ForgotPass () {
    return (
        <div className='ForgotForm'>
            <img src='R11.png' alt=''/>
            <form action='' className='FormForgot' >
            <h1>Forgot Password</h1>
            <div className='InputForm'>
             <input type='email' placeholder='E_mail'/>
             <Email className='iconFormForgot'/>
             </div>
             <div className='ForgotLink'>
             <p>Nécessite que votre adresse e-mail principale ou secondaire soit vérifiée dans <b style={{color:'rgb(63, 145, 252)'}}>Bin_Edk</b>.</p>
             </div>
            <button type='submit'>Reset Password</button>
            <div className='ForgotLink'>
            <p>Already have an account? <Link to={'/login'}>Login</Link></p>
            </div>
            </form>
        </div>
    );
}
