import React from 'react'
import './experience.css'
import { useState } from 'react'
function Experience() {
  const [isPost,setPost]=useState(false)
  function handelClick(){
       
  }
  return (
    <div className='experienceTop'>
         <div className="containerEX">
         {!isPost && 
          <div className="postEx">
            <div className="textEX">Thanks for rating us!</div>
            <div className="editEx">EDIT</div>
          </div>
          }
            <div className="star-widget">
                <input type="radio" name='rate' id='rate-5' />
                <label for='rate-5' className='fas fa-star'></label>
                <input type="radio" name='rate' id='rate-4' />
                <label for='rate-4' className='fas fa-star'></label>
                <input type="radio" name='rate' id='rate-3' />
                <label for='rate-3' className='fas fa-star'></label>
                <input type="radio" name='rate' id='rate-2' />
                <label for='rate-2' className='fas fa-star'></label>
                <input type="radio" name='rate' id='rate-1' />
                <label for='rate-1' className='fas fa-star'></label>
            <form onSubmit={(e)=>e.preventDefault()} action="#">
                 <header></header>
                 <div className="textarea">
                  <textarea cols="30" placeholder='Describe your experience..'></textarea>
                 </div>
                 <div className="btn">
                  <button onClick={handelClick()} type='submit'>Post</button>
                 </div>
            </form>
            </div>
         </div>

{/* 
         <script>
            const btn =document.querySelector("button"); 
            const post =document.querySelector(".postEx"); 
            const widget =document.querySelector(".star-widget"); 
            const edit =document.querySelector(".editEx"); 
            btn.onclick = () =>{
               widget.style.display = "none";
               post.style.display = "block";
            edit.onclick = () =>{
               widget.style.display = "block";
               post.style.display = "none";
            }
          }
            
         </script> */}
    </div>
  )
}

export default Experience
