import React, { useState } from 'react';
import './experience.css'; 

function Experience() {
  const [isEditing, setIsEditing] = useState(true);
  const [message, setMessage] = useState(""); 
  const [theme, setTheme] = useState("");
  const [star, setStar] = useState("");

  const handlePostClick = () => {
    if (star !== "") {
      const starInfo = { star, theme };
      fetch("http://localhost:3000/experience", {
        method: "POST",
        headers: {'content-type' : 'application/json'},
        body: JSON.stringify(starInfo)
      })
      .then((res) => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then((data) => {
        setIsEditing(false);
        setMessage("Merci d'avoir évalué notre service !");
        alert("Merci d'avoir évalué notre service !");
      })
      .catch((err) => {
        alert('Failed: ' + err.message);
      });
    } else {
      alert('Veuillez sélectionner une évaluation en étoiles.');
    }
  };
  
  const handleEditClick = () => {
    setIsEditing(true); 
  };

  return (
    <div className='experienceTop'>
      <div className="containerEX">
        <div className={isEditing ? "postEx hidden" : "postEx"}>
          <div className="textEX">{message}</div>
          <div className="editEx" onClick={handleEditClick}>EDIT</div>
        </div>
        <div className={isEditing ? "star-widget" : "star-widget hidden"}>
          <input onClick={() => setStar("5")} type="radio" name='rate' id='rate-5' checked={star === "5"} />
          <label htmlFor='rate-5' className='fas fa-star'></label>
          <input onClick={() => setStar("5")} type="radio" name='rate' id='rate-4' checked={star === "5"} />
          <label htmlFor='rate-4' className='fas fa-star'></label>
          <input onClick={() => setStar("5")} type="radio" name='rate' id='rate-3' checked={star === "5"} />
          <label htmlFor='rate-3' className='fas fa-star'></label>
          <input onClick={() => setStar("5")} type="radio" name='rate' id='rate-2' checked={star === "5"} />
          <label htmlFor='rate-2' className='fas fa-star'></label>
          <input onClick={() => setStar("5")} type="radio" name='rate' id='rate-1' checked={star === "5"} />
          <label htmlFor='rate-1' className='fas fa-star'></label>
          <form onSubmit={(e) => e.preventDefault()}>
            <header></header>
            <div className="textarea">
              <textarea value={theme} onChange={(e) => setTheme(e.target.value)} cols="30" placeholder='Décrivez votre expérience..'></textarea>
            </div>
            <div className="btn">
              {isEditing && <button type='submit' onClick={handlePostClick}>Post</button>}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Experience;
