import React, { useState } from 'react';
import axios from 'axios';
import './whattsappCall.css';

const WhatSappCall = () => {
  const [fullName, setFullName] = useState('');
  const [city, setCity] = useState('Laayoune');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [theme, setTheme] = useState('');
  const [showDivCall, setShowDivCall] = useState(false);
  const [showDivFormCall, setShowDivFormCall] = useState(true);
  const phoneNumber = '212774463661';
  const whatsappLink = `https://web.whatsapp.com/send?phone=${phoneNumber}`;

  const handleChatClick = () => {
    window.open(whatsappLink, '_blank');
  };

  const handleFormCallClose = () => {
    setShowDivCall(true);
    setShowDivFormCall(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validation des données avant envoi
    if (!validateEmail(email)) {
      alert("L'adresse email n'est pas valide. Veuillez la corriger.");
      return;
    }

    // Construction du corps du message
    const messageBody = `
      Demande de rappel
      <p><strong>Nom &amp; Prénom:</strong> ${fullName}</p>
      <p><strong>Téléphone:</strong> ${phone}</p>
      <p><strong>Email:</strong> ${email}</p>
      <p><strong>Description du problème:</strong> ${theme}</p>
    `;

    try {
      // Envoi de la demande par email
      await axios.post('http://localhost:3000/contact', {
        subject: 'Demande de rappel',
        text: messageBody,
        html: messageBody,
        from: 'BIN_EDK Web',
        to: ['oumaima@binedk.ma','binedk@binedk.ma'],
        cc: ['hayat@binedk.ma']
      });
      alert('Votre demande de rappel a été envoyée avec succès !');
    } catch (error) {
      console.error('Erreur lors de l\'envoi de l\'email :', error);
      alert("Une erreur s'est produite lors de l'envoi de votre demande. Veuillez réessayer plus tard.");
    }
  };

  const validateEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  return (
    <div id="divFormCall" className="div-block-234" style={{ display: 'block' }}>
      <div className="div-block-269">
        <div className="div-block-235" style={{ backgroundColor: 'black' }}>
          <img style={{ marginRight: '18px' }} src="assistance.png" loading="lazy" width="78" alt="" className="image-112" />
          <div className="text-block-163">Discutez avec un agent</div>
          {showDivCall && (
            <div id="divCall">
            </div>
          )}
          {showDivFormCall && (
            <div id="divFormCall">
            </div>
          )}
          <div id="btnFormCallClose" className="div-block-237" onClick={handleFormCallClose}>
            <img src="arrow-down-sign-to-navigate-1.png" loading="lazy" width="11" alt="" />
          </div>
        </div>
        <div className="div-block-247">
          <div className="form-block-3 w-form">
            <form id="formCallMe" onSubmit={handleSubmit} className="form-4" aria-label="Formulaire de rappel">
              <label htmlFor="fullName" className="field-label">Nom &amp; prénom</label>
              <input type="text" className="text-field-6 w-input" autoComplete="off" maxLength="256" name="fullName" placeholder="Entrer votre nom" id="fullName" value={fullName} onChange={(e) => setFullName(e.target.value)} required />
              <label htmlFor="city" className="field-label">Ville</label>
              <select id="city" name="field-2" data-name="Field 2" className="text-field-6 w-select" value={city} onChange={(e) => setCity(e.target.value)} required>
                <option value="Casablanca">Laâyoune</option>
                <option value="Rabat">Boujdour</option>
                <option value="Marrakech">Es-Semara</option>
              </select>
              <label htmlFor="phone" className="field-label">Tél.</label>
              <input type="text" className="text-field-6 w-input" autoComplete="off" maxLength="256" name="phone" placeholder="Entrer votre téléphone" id="phone" value={phone} onChange={(e) => setPhone(e.target.value)} required />

              <label htmlFor="email" className="field-label">Email</label>
              <input type="text" className="text-field-6 w-input" autoComplete="off" maxLength="256" name="email" placeholder="Entrer votre adresse email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

              <label htmlFor="theme" className="field-label">Description</label>
              <textarea style={{ resize: 'none', height: '100px' }} className="text-field-6 w-input" autoComplete="off" maxLength="256" name="theme" placeholder="Décrivez votre problème" id="theme" value={theme} onChange={(e) => setTheme(e.target.value)} required></textarea>

              <div className="div-block-236">
                <button type="submit" id="btnCallMe" style={{ backgroundColor: 'black', color: 'white', height: '40px', fontSize: '13px',paddingTop:'-10px' }} className="button-20 w-button">
                  <a href={handleChatClick}><i className='fab fa-whatsapp icon'></i></a>Discutez avec moi !
                </button>
              </div>
            </form>
            <div className="success-message-4 w-form-done" tabIndex="-1" role="region" aria-label="Succès de la demande de rappel">
              <div style={{ color: 'green' }}>Merci, votre demande a bien été envoyée</div>
            </div>
            <div className="error-message-7 w-form-fail" tabIndex="-1" role="region" aria-label="Échec de la demande de rappel">
              <div style={{ color: 'red' }}>Une erreur s'est produite lors de l'envoi de votre demande. Veuillez réessayer plus tard.</div>
            </div>
          </div>
          <div id="messageRappel" className="div-block-279">
            <div style={{ color: 'blue' }} className="text-block-177">Votre demande de rappel a bien été envoyée !</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WhatSappCall;
