import React, { useState } from 'react';
import { z } from 'zod';
import { Lock, Person } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import './login.css';

const formSchema = z.object({
  email: z.string().email().min(2, "Email must be at least 2 characters long").max(30, "Email must be at most 30 characters long"),
  password: z.string().min(8, "Password must be at least 8 characters long").max(30, "Password must be at most 30 characters long")
});

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleCheckboxChange = () => {
    setRememberMe(!rememberMe);
  };

  const sendEmail = (e) => {
    e.preventDefault();
    setEmailError("");
    setPasswordError("");
    const formData = { email, password ,rememberMe };

    if (validation()){
      //implentation
      fetch("http://localhost:3000/login/"+email).then((res)=>{
        return res.json();
      }).then((resp)=>{
        console.log(resp)
      }).catch((err)=>{
        alert("Login Failed due to :"+ err.message);
      })
    }






    try {
      formSchema.parse(formData);
      console.log("Form data is valid:", formData);
      // Here you can continue with your login logic
    } catch (error) {
      error.errors.forEach((err) => {
        if (err.path[0] === 'email') {
          setEmailError(err.message);
        } else if (err.path[0] === 'password') {
          setPasswordError(err.message);
        }
      });
      console.error("Form validation failed:", error.errors);
    }
  };

  return (
    <div className='loginTop'>
      <form action='' className='loginForm' onSubmit={sendEmail}>
        <h1>Connectez-vous</h1>
        <div className='inputBox'>
          <input type='email' placeholder='Nom dâ€™utilisateur' value={email} onChange={(e) => setEmail(e.target.value)} required />
          <Person className='iconLogin' />
          {emailError && <span style={{ color: 'red' }}>{emailError}</span>}
        </div>
        <div className='inputBox'>
          <input type='password' placeholder='Mot de passe' value={password} onChange={(e) => setPassword(e.target.value)} required />
          <Lock className='iconLogin' />
          {passwordError && <span style={{ color: 'red' }}>{passwordError}</span>}
        </div>
        <div className="rememberForgot">
          <label className='labelLogin'><input className='checkbox' type="checkbox" checked={rememberMe} onChange={handleCheckboxChange} />Rappelle-moi</label>
          <Link to={'/forgotPass'}>Mot de passe oubliÃ©?</Link>
        </div>
        <button type='submit'>Login</button>
        <div className='registerLink'>
          <p> Vous nâ€™avez pas de compte? <Link to={'/register'}>Register</Link></p>
        </div>
      </form>
    </div>
  )
}

export default Login;
