import React, { useState } from 'react';
import { Lock, Person } from '@mui/icons-material';
import { Link, useNavigate } from 'react-router-dom';
import './login.css';
import Header from '../../components/common/heading/Header';
import Footer from '../../components/footer/Footer';
import { useDispatch } from 'react-redux';
import { login } from '../../redux/UserSlice';

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleCheckboxChange = () => {
    setRememberMe(!rememberMe);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validate()) {
      fetch("http://localhost:3000/register?email=" + email)
        .then((res) => res.json())
        .then((resp) => {
          if (resp.length > 0 && resp[0].password === password) {
            alert('Success');
            sessionStorage.setItem('email', email);
            dispatch(login({
              email: email,
              password: password,
              loggedIn: true,
            }));
            navigate('/home');
          } else {
            alert('Please Enter Valid Credentials');
          }
        })
        .catch((err) => {
          alert('login Failed due to: ' + err.message);
        });
    }
  };

  const validate = () => {
    let isValid = true;
    if (!email.trim()) {
      alert('Please Enter Valid email');
      isValid = false;
    }
    if (!password.trim()) {
      alert('Please Enter Password');
      isValid = false;
    }
    return isValid;
  }

  return (
    <div className='loginTopPage'>
      <Header />
      <div className='loginTop'>
        <form action='' className='loginForm' onSubmit={handleSubmit}>
          <h1>Connectez-vous</h1>
          <div className='inputBox'>
            <input type='email' placeholder='Nom dâ€™utilisateur' value={email} onChange={(e) => setEmail(e.target.value)} required />
            <Person className='iconLogin' />
          </div>
          <div className='inputBox'>
            <input type='password' placeholder='Mot de passe' value={password} onChange={(e) => setPassword(e.target.value)} required />
            <Lock className='iconLogin' />
          </div>
          <div className="rememberForgot">
            <label className='labelLogin'>
              <input className='checkbox' type="checkbox" checked={rememberMe} onChange={handleCheckboxChange} />
              Rappelle-moi
            </label>
            <Link to={'/forgotPass'}>Mot de passe oubliÃ©?</Link>
          </div>
          <button type='submit'>Login</button>
          <div className='registerLink'>
            <p> Vous nâ€™avez pas de compte? <Link to={'/register'}>Register</Link></p>
          </div>
        </form>
      </div>
      <Footer />
    </div>
  );
}

export default Login;
