import { Email, Lock, Person } from '@mui/icons-material';
import { z } from 'zod';
import React, { useState } from 'react';
import './register.css'
import { Link } from 'react-router-dom';

const formSchema = z.object({
    email: z.string().email().min(2, "Email must be at least 2 characters long").max(30, "Email must be at most 30 characters long"),
    password: z.string().min(8, "Password must be at least 8 characters long").max(30, "Password must be at most 30 characters long")
});

export default function Register() {
    const [prenom, setPrenom] = useState("");
    const [nom, setNom] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");

    
    const sendEmail = (e) => {
        e.preventDefault();
        setEmailError("");
        setPasswordError("");

        const formData = {
            prenom,
            nom,
            email,
            password
        };
        fetch("http://localhost:3000/register",{
            method:"POST",
            headers:{'content-type' : 'application/json'},
            body: JSON.stringify(formData)
        }).then((res)=>{
            alert('Registered successfully.')
        }).catch((err)=>{
            alert('Failed :'+ err.message)
        });




        try {
            formSchema.parse(formData);
            console.log("Form data is valid:", formData);
        } catch (error) {
            error.errors.forEach((err) => {
                if (err.path[0] === 'email') {
                    setEmailError(err.message);
                } else if (err.path[0] === 'password') {
                    setPasswordError(err.message);
                }
            });
            console.error("Form validation failed:", error.errors);
        }
    };
    return (
        <div className='RegisterTop'>
            <form method='POST' action='' className='RegisterForm' onSubmit={sendEmail}>
                <h1 >Register</h1>
                <div className='RegisterInput'>
                    <input type='text' placeholder='Prénom' value={prenom} onChange={(e) => setPrenom(e.target.value)} required />
                    <Person className='iconsRegister' />
                </div>
                <div className='RegisterInput'>
                    <input type='text' placeholder='Nom' value={nom} onChange={(e) => setNom(e.target.value)} required />
                    <Person className='iconsRegister' />
                </div>
                <div className='RegisterInput'>
                    <input type='email' placeholder='E_mail' value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <Email className='iconsRegister' />
                    {emailError && <span style={{ color: 'red' }}>{emailError}</span>}
                </div>
                <div className='RegisterInput'>
                    <input type='password' placeholder='Mot de passe' value={password} onChange={(e) => setPassword(e.target.value)} required />
                    <Lock className='iconsRegister' />
                    {passwordError && <span style={{ color: 'red' }}>{passwordError}</span>}
                </div>
                <button type='submit'>Register</button>
                <div className='LoginLink'>
                    <p >Avez-vous un compte ? <Link to={'/login'}>Connectez-vous</Link></p>
                </div>
            </form>
        </div>
    );
}
