import React, { useState } from 'react';
import './experience.css';

const Experience = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [experienceText, setExperienceText] = useState('');

  const handlePostClick = () => {
    setIsEditing(true);
  };

  const handleEditClick = () => {
    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    setExperienceText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Vous pouvez ajouter ici la logique pour soumettre l'expérience
    console.log('Expérience soumise:', experienceText);
  };

  return (
    <div className='experienceTop'>
      <div className="containerEX">
        {isEditing ? (
          <div className="postEx">
            <div className="textEX">Thanks for rating us!</div>
            <div className="editEx" onClick={handleEditClick}>EDIT</div>
          </div>
        ) : (
          <div className="star-widget">
            <input type="radio" name='rate' id='rate-5' />
            <label htmlFor='rate-5' className='fas fa-star'></label>
            <input type="radio" name='rate' id='rate-5' />
            <label htmlFor='rate-4' className='fas fa-star'></label>
            <input type="radio" name='rate' id='rate-5' />
            <label htmlFor='rate-5' className='fas fa-star'></label>
            <input type="radio" name='rate' id='rate-5' />
            <label htmlFor='rate-5' className='fas fa-star'></label>
            {/* Ajoutez d'autres étoiles ici */}
            <form onSubmit={handleSubmit}>
              <header></header>
              <div className="textarea">
                <textarea cols="30" placeholder='Describe your experience..' onChange={handleInputChange}></textarea>
              </div>
              <div className="btn">
                <button type='submit' onClick={handlePostClick}>Post</button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

export default Experience;
