import React, { Component } from 'react';
import './style.css';
class Popup  {
  componentDidMount() {
    setTimeout(() => {
      this.popup.style.display = "block";
    }, 3000);
  }

  handleClose = () => {
    this.popup.style.display = "none";
  }
  render() {
    return (
      <div className="popup" ref={ref => this.popup = ref}>
        <div className="contentBox">
          <div className="close" onClick={this.handleClose}></div>
          <div className="imgBx">
            <img src="whatsapp.png" alt="" />
          </div>
          <div class="content">
                <div>
                    <h3>Special Offer</h3>
                    <h2>80 <sup>%</sup><span>Off</span></h2>
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Magnam, eos?</p>
                    <a href="#">Get the Deal</a>
                </div>
            </div>
        </div>
      </div>
    );
  }
}

export default Popup;
