import React, { useEffect } from 'react';
import './experience.css';

function Experience() {
  useEffect(() => {
    const btn = document.querySelector("button");
    const post = document.querySelector(".postEx");
    const widget = document.querySelector(".star-widget");
    const edit = document.querySelector(".editEx");

    const handleButtonClick = () => {
      widget.style.display = "none";
      post.style.display = "block";
    };

    const handleEditClick = () => {
      widget.style.display = "block";
      post.style.display = "none";
    };

    btn.addEventListener('click', handleButtonClick);
    edit.addEventListener('click', handleEditClick);

    return () => {
      btn.removeEventListener('click', handleButtonClick);
      edit.removeEventListener('click', handleEditClick);
    };
  }, []);

  return (
    <div className='experienceTop'>
      <div className="containerEX">
        <div className="postEx">
          <div className="textEX">Thanks for rating us!</div>
          <div className="editEx" onChange={handleEditClick()}>EDIT</div>
        </div>
        <div className="star-widget">
          <input type="radio" name='rate' id='rate-5' />
          <label htmlFor='rate-5' className='fas fa-star'></label>
          <input type="radio" name='rate' id='rate-4' />
          <label htmlFor='rate-4' className='fas fa-star'></label>
          <input type="radio" name='rate' id='rate-3' />
          <label htmlFor='rate-3' className='fas fa-star'></label>
          <input type="radio" name='rate' id='rate-2' />
          <label htmlFor='rate-2' className='fas fa-star'></label>
          <input type="radio" name='rate' id='rate-1' />
          <label htmlFor='rate-1' className='fas fa-star'></label>
          <form action="#">
            <header></header>
            <div className="textarea">
              <textarea cols="30" placeholder='Describe your experience..'></textarea>
            </div>
            <div className="btn">
              <button onClick={handleButtonClick()} type='submit'>Post</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Experience;
