import React, { useState } from 'react';
import { Lock, Person } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { loginUser } from '../redux/store/UserSlice'; // Assurez-vous d'avoir le chemin correct
import './login.css';

function Login() {
    const dispatch = useDispatch();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Dispatch the loginUser action with the username and password
        dispatch(loginUser({ username, password }));
    };

    return (
        <div className='loginTop'>
            <form className='loginForm' onSubmit={handleSubmit}>
                <h1>Connectez-vous</h1>
                <div className='inputBox'>
                    <input
                        type='text'
                        placeholder='Nom d’utilisateur'
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <Person className='iconLogin' />
                </div>
                <div className='inputBox'>
                    <input
                        type='password'
                        placeholder='Mot de passe'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <Lock className='iconLogin' />
                </div>
                <div className='rememberForgot'>
                    <label className='labelLogin'>
                        <input className='checkbox' type='checkbox' />Rappelle-moi
                    </label>
                    <Link to={'/forgotPass'}>Mot de passe oublié?</Link>
                </div>
                <button type='submit'>Login</button>
                <div className='registerLink'>
                    <p>
                        Vous n’avez pas de compte? <Link to={'/register'}>Register</Link>
                    </p>
                </div>
            </form>
        </div>
    );
}

export default Login;
