import React, { useState } from 'react';
import './experience.css'; 

function Experience() {
  const [isEditing, setIsEditing] = useState(true);
  const [message, setMessage] = useState(""); 
  const [theme,setTheme]= useState("");
  const [star,setStar]=useState("");
  const handlePostClick = () => {
    const message = "Merci d'avoir évalué notre service !";
    setIsEditing(false); 
    setMessage(message);
    alert(message); 
  };
  
  const handleEditClick = () => {
    setIsEditing(true); 
  };


const formInfo = {
  isEditing,
  message
  };
  if (IsValidate()) {
      fetch("http://localhost:3000/experience", {
          method: "POST",
          headers: {'content-type' : 'application/json'},
          body: JSON.stringify(formInfo)
      })
      .then((res) => {
          alert('successfully.');
          navigate('/');
      })
      .catch((err) => {
          alert('Failed: ' + err.message);
      });
  };
  return (
    <div className='experienceTop'>
      <div className="containerEX">
        <div className={isEditing ? "postEx hidden" : "postEx"}>
          <div className="textEX">{message}</div>
          <div className="editEx" onClick={handleEditClick}>EDIT</div>
        </div>
        <div className={isEditing ? "star-widget" : "star-widget hidden"}>
          <input value={star} onClick={(e)=>setStar(e.target.value)} type="radio" name='rate' id='rate-5' />
          <label htmlFor='rate-5' className='fas fa-star'></label>
          <input value={star} onClick={(e)=>setStar(e.target.value)} type="radio" name='rate' id='rate-4' />
          <label htmlFor='rate-4' className='fas fa-star'></label>
          <input value={star} onClick={(e)=>setStar(e.target.value)} type="radio" name='rate' id='rate-3' />
          <label htmlFor='rate-3' className='fas fa-star'></label>
          <input type="radio" name='rate' id='rate-2' />
          <label htmlFor='rate-2' className='fas fa-star'></label>
          <input type="radio" name='rate' id='rate-1' />
          <label htmlFor='rate-1' className='fas fa-star'></label>
          <form onSubmit={(e) => e.preventDefault()}>
            <header></header>
            <div className="textarea" >
              <textarea value={theme} onClick={(e)=>setTheme(e.target.value)} cols="30" placeholder='Décrivez votre expérience..'></textarea>
            </div>
            <div className="btn">
              <button type='submit' onClick={handlePostClick}>Post</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Experience;
