import React,{useState} from 'react'
import { Lock, Person } from '@mui/icons-material';
import { Link, useNavigate } from 'react-router-dom';
import './login.css'
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from '../../redux/store/UserSlice';
function Login() {
    const [email,setEmail]=useState('');
    const [password,setPassword]=useState('');
    const {laoding,error}=useSelector((state)=>state.user);
    const dispatch =useDispatch();
    const navigation=useNavigate();
    const sendEmail = (e) => {
        e.preventDefault();
        let userCredentials={
            email,password
        }
        dispatch(loginUser(userCredentials)).then((result)=>{
            if(result.payload){
                setEmail('');
                setPassword('');
                Navigate('/')
            }
        })
    };
  return (
    <div className='loginTop '>
            <form action='' className='loginForm'  onSubmit={sendEmail}>
                <h1>Connectez-vous</h1>
                <div className='inputBox'>
                    <input type='text' placeholder='Nom d’utilisateur' required value={email} onChange={(e)=>setEmail(e.target.value)} />
                    <Person className='iconLogin' />
                </div>
                <div className='inputBox'>
                    <input type='password' placeholder='Mot de passe' required  value={password} onChange={(e)=>setPassword(e.target.value)}/>
                    <Lock className='iconLogin' />
                </div>
                <div className="rememberForgot">
                <label className='labelLogin'><input className='checkbox' type="checkbox" />Rappelle-moi</label>
                    <Link to={'/forgotPass'}>Mot de passe oublié?</Link>
                </div>
                <button type='submit'>{laoding ?'Loading....':'Login' }</button>
                {error&&(
                    <div className="alert alert-danger" role='alert'>{error}</div>
                )}
                <div className='registerLink'>
                    <p> Vous n’avez pas de compte? <Link to={'/register'}>Register</Link></p>
                </div>
            </form>

        </div>
  )
}

export default Login
