import React, { useEffect, useState } from 'react'
import "./backgroundSlider.css"
import imageSlider from '../data.js'
export default function BackgroundSlider() {
    const[currentState,setCurrentState]=useState(0);
    useEffect(()=>{
        const timer =setTimeout(()=>{
             if(currentState===2){
                setCurrentState(0)
             }else{
                setCurrentState(currentState+1)
             }
        },4000)
        return()=>clearTimeout(timer)
    },[currentState])
    const bgImageStyle={
        backgroundImage:`URL(${imageSlider[currentState].URL})`,
        backgroundPosition:'center',
        backgroundSize:'cover',
        height:'100%'
    }
    const gotoNext =(currentState)=>{
        setCurrentState(currentState)
    }
  return (
    <div className='continer-style'>
        <div style={bgImageStyle}></div>
        <div className="transparent-background"></div>
        <div className="description">
            <div>
                <h1>{imageSlider[currentState].titre}</h1>
                <p>{imageSlider[currentState].body}</p>
            </div>
            <div className="carousel-boullt">
                {
                    imageSlider.map((imageSlider,currentState)=>(
                        <span key={currentState} onClick={()=>gotoNext(currentState)}></span>
                    ))
                }
            </div>
        </div>
    </div>
  )
}
