import React, { useState } from 'react';
import { Email, Lock, Person } from '@mui/icons-material';
import './register.css';
import { Link, useNavigate } from 'react-router-dom';

export default function Register() {
    const [prenom, setPrenom] = useState("");
    const [nom, setNom] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const IsValidate = () => {
        let isProceed = true;
        let errorMessage = 'Please enter the value into';
        if (!prenom.trim()) {
            isProceed = false;
            errorMessage += " Prénom ";
        }
        if (!nom.trim()) {
            isProceed = false;
            errorMessage += " Nom ";
        }
        if (!email.trim()) {
            isProceed = false;
            errorMessage += " Email ";
        }
        if (!password.trim()) {
            isProceed = false;
            errorMessage += " Password ";
        }
        if (!isProceed) {
            alert(errorMessage);
        } else {
            if (/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
                // Email is valid
            } else {
                isProceed = false;
                alert('Please enter a valid email.');
            }
            if (/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*]).{8,}$/.test(password)) {
                // Password is valid
            } else {
                isProceed = false;
                alert('Password must contain at least 8 characters, including one uppercase letter, one lowercase letter, one number, and one special character from !@#$%^&*');
            }
        }
        return isProceed;
    }
    
    const sendEmail = (e) => {
        e.preventDefault();
        const formData = {
            prenom,
            nom,
            email,
            password
        };
        if (IsValidate()) {
            fetch("http://localhost:3000/register", {
                method: "POST",
                headers: {'content-type' : 'application/json'},
                body: JSON.stringify(formData)
            })
            .then((res) => {
                alert('Registered successfully.');
                navigate('/login');
            })
            .catch((err) => {
                alert('Failed: ' + err.message);
            });
        }
    };

    return (
        <div className='RegisterTop'>
            <form className='RegisterForm' onSubmit={sendEmail}>
                <h1>Register</h1>
                <div className='RegisterInput'>
                    <input type='text' placeholder='First Name' value={prenom} onChange={(e) => setPrenom(e.target.value)} required />
                    <Person className='iconsRegister' />
                </div>
                <div className='RegisterInput'>
                    <input type='text' placeholder='Last Name' value={nom} onChange={(e) => setNom(e.target.value)} required />
                    <Person className='iconsRegister' />
                </div>
                <div className='RegisterInput'>
                    <input type='email' placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <Email className='iconsRegister' />
                </div>
                <div className='RegisterInput'>
                    <input type='password' placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} required />
                    <Lock className='iconsRegister' />
                </div>
                <button type='submit'>Register</button>
                <div className='LoginLink'>
                    <p>Already have an account? <Link to={'/login'}>Login</Link></p>
                </div>
            </form>
        </div>
    );
}
