import React, { useState } from 'react';
import './contact.css';
import { useNavigate } from 'react-router-dom';

function Contact() {
  const [nom, setNom] = useState('');
  const [email, setEmail] = useState('');
  const [theme, setTheme] = useState('');
  const [star, setStar] = useState('');
  const navigate = useNavigate();

  const IsValidate = () => {
    let isProceed = true;
    let errorMessage = 'Please enter a value for:';
    if (nom.trim() === '') {
      isProceed = false;
      errorMessage += ' Nom & Prenom';
    }
    if (email.trim() === '') {
      isProceed = false;
      errorMessage += ' Email';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      isProceed = false;
      errorMessage += ' Valid Email';
    }
    if (!isProceed) {
      alert(errorMessage);
    }
    return isProceed;
  };

  const sendEmail = (e) => {
    e.preventDefault();
    const formInfo = {
      nom,
      email,
      theme,
      star,
    };
    if (IsValidate()) {
      fetch('http://localhost:3000/contact', {
        method: 'POST',
        headers: { 'content-type': 'application/json' },
        body: JSON.stringify(formInfo),
      })
        .then((res) => {
          if (res.ok) {
            alert('Email sent successfully.');
            navigate('/');
          } else {
            throw new Error('Failed to send email.');
          }
        })
        .catch((err) => {
          alert('Failed: ' + err.message);
        });
    }
  };

  return (
    <div className='contactForm' id='contact'>
      <form onSubmit={sendEmail}>
        <h1 className='titleContact'>Se Connecter Avec Nous</h1>
        <div className='input_Contact'>
          <i className='fas fa-user icons'></i>
          <input
            placeholder='Nom & Prenom'
            value={nom}
            onChange={(e) => setNom(e.target.value)}
            type='text'
            name='user_name'
            required
          />
        </div>

        <div className='input_Contact'>
          <i className='fas fa-envelope icons'></i>
          <input
            placeholder='Email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type='email'
            name='user_email'
            required
          />
        </div>
        <div className='input_Contact'>
          <i className='fas fa-comments icons'></i>
          <textarea
            placeholder='Votre Message'
            value={theme}
            onChange={(e) => setTheme(e.target.value)}
            id='message'
            name='message'
            rows={4}
            required
          />
        </div>
        <div className='star-widget'>
          {/* Example of rendering star rating */}
          <p>Please rate your experience:</p>
          {[...Array(5)].map((_, index) => (
            <label key={index}>
              <input
                type='radio'
                name='rating'
                value={index + 1}
                checked={star === `${index + 1}`}
                onChange={(e) => setStar(e.target.value)}
              />
              {index + 1}
              <span className='icon'>â˜…</span>
            </label>
          ))}
        </div>
        <button className='buttonSubmit' type='submit'>
          <i className='fas fa-paper-plane icons-button '></i>Submit
        </button>
      </form>
    </div>
  );
}

export default Contact;
