import React, { useState } from 'react'
import Header from '../../../../common/heading/Header'
import Footer from '../../../../footer/Footer'
import { useDispatch } from 'react-redux'
import { decrementer, incrementer } from '../../../../../redux/UserSlice';
function ClimatisationLink() {
    const dispatch = useDispatch();
    const [cli,setcli]=useState("0")
    return (
        <div className='climatisationLinkTopComponent'>
            <Header />
            <div className='climatisationLinkTop'>
                <h1>Climatisation</h1>
                <p>Pouvez-vous préciser le type d'extracteur?</p>
                <div>
                {mesArticles.map((art,index)=>{
                        return(
                            <tr key={index}>
                                <td>{art.id}</td>
                                <td> {art.nom} </td>
                                <td>{art.prix} </td>
                                <td><button className='btn btn-secondary m-2' onClick={()=>dispatch(decrementer( {id: art.id} ))}>-</button>
                                {art.quantite}
                                <button className="btn btn-primary m-2" onClick={()=>dispatch(incrementer({id: art.id} ))}>+</button></td>
                                <td><button className="btn btn-danger" onClick={()=>dispatch(deleteArticle(art.id))}>Supprimer</button></td>
                            </tr>
                        )
                    })}
                </div>
                <div>
                    <p>Extracteur électrique</p>
                </div>
            </div>
            <Footer />
        </div>
    )
}

export default ClimatisationLink
