import React, { useState } from 'react';
import './src\components\whatSappCall\whattsappCall.css';

const WhatSappCall = () => {
  const [fullName, setFullName] = useState('');
  const [city, setCity] = useState('Laayoune');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [theme, setTheme] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // تحقق من صحة البريد الإلكتروني باستخدام Regex
    const emailIsValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    if (!emailIsValid) {
      alert("L'adresse email n'est pas valide. Veuillez la corriger.");
      return;
    }

    // بناء الرسالة
    const messageBody = `
      Demande de rappel
      Nom & Prénom: ${fullName}
      Téléphone: ${phone}
      Email: ${email}
      Description du problème: ${theme}
    `;

    try {
      // إرسال الرسالة باستخدام fetch
      const response = await fetch('http://localhost:3000/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          subject: 'Demande de rappel',
          text: messageBody,
          html: messageBody,
          from: 'BIN_EDK Web',
          to: ['oumaima@binedk.ma', 'binedk@binedk.ma'],
          cc: ['hayat@binedk.ma']
        })
      });

      if (response.ok) {
        alert('Votre demande de rappel a été envoyée avec succès !');
      } else {
        alert('Une erreur s\'est produite lors de l\'envoi de votre demande. Veuillez réessayer plus tard.');
      }
    } catch (error) {
      console.error('Erreur lors de l\'envoi de l\'email :', error);
      alert('Une erreur s\'est produite lors de l\'envoi de votre demande. Veuillez réessayer plus tard.');
    }
  };

  return (
    <div className="whatsapp-call-container">
      <form onSubmit={handleSubmit} className="call-form">
        <label>Nom & Prénom</label>
        <input type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} required />
        
        <label>Ville</label>
        <select value={city} onChange={(e) => setCity(e.target.value)} required>
          <option value="Laayoune">Laâyoune</option>
          <option value="Boujdour">Boujdour</option>
          <option value="Es-Semara">Es-Semara</option>
        </select>
        
        <label>Téléphone</label>
        <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} required />

        <label>Email</label>
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <label>Description</label>
        <textarea value={theme} onChange={(e) => setTheme(e.target.value)} required></textarea>

        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
};

export default WhatSappCall;
