import React, { useState } from 'react';
import './whattsappCall.css'

const WhatsAppCall = () => {
  const [formData, setFormData] = useState({
    nom: '',
    ville: 'Laâyoune',
    tele: '',
    email: '',
    message: ''
  });
  const [isFormVisible, setIsFormVisible] = useState(false);
  const toggleFormVisibility = () => {
    setIsFormVisible(prevState => !prevState);
  };
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handeleSubmit = (e) => {
    e.preventDefault();
  }

  const sendWhatsApp = () => {
    const { nom, ville, tele, email, message } = formData;
    const phoneNumber = "+774463661";
    const url = "https://wa.me/" + phoneNumber + "?text=" +
      "Nom : " + nom + "%0a" +
      "Ville : " + ville + "%0a" +
      "Telephone: " + tele + "%0a" +
      "Email : " + email + "%0a" +
      "Message : " + message + "%0a" +
      "%0a%0a";
    fetch("http://localhost:3000/whatsappCall", {
      method: "POST",
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify(formData)
    })
    if (!nom.trim()) {
      alert("Le champ Nom & prénom est requis. Veuillez le remplir.");
      return;
    }
    if (tele.length !== 10) {
      alert("Le numéro de téléphone doit contenir 10 chiffres. Veuillez le corriger.");
      return;
    }
    if (!validateEmail(email)) {
      alert("L'adresse email n'est pas valide. Veuillez la corriger.");
      return;
    }
    if (!message.trim()) {
      alert("Le champ Description est requis. Veuillez le remplir.");
      return;
    }
    window.open(url, '_blank').focus();
  };

  const validateEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  return (
    <div id="divFormCall" className="div-block-234" style={{ display: 'block' }}>
      <div className="div-block-269">
        <div className="div-block-235" style={{ backgroundColor: 'black' }}>
          <img style={{ marginRight: '18px' }} src="assistance.png" loading="lazy" width="78" alt="" className="image-112" />
          <div className="text-block-163">Discutez avec un agent</div>
          <div id="btnFormCallClose" className="div-block-237" >

            {/* arrow-down */}
            <img src="arrow-down-sign-to-navigate-1.png" loading="lazy" width="11" alt="" />
          </div>
        </div>
        <div className="div-block-247">
          <div className="form-block-3 w-form">
            <form onSubmit={handeleSubmit} id="formCallMe" className="form-4" aria-label="Formulaire de rappel">
              <label  className="field-label">Nom &amp; prénom</label>
              <input type="text" className="text-field-6 w-input" name="nom" placeholder="Entrer votre nom" id="nom" value={formData.nom} onChange={handleChange} required />
              <label  className="field-label">Ville</label>
              <select id="ville" name="ville" className="text-field-6 w-select" value={formData.ville} onChange={handleChange} required>
                <option value="Laâyoune">Laâyoune</option>
                <option value="Boujdour">Boujdour</option>
                <option value="Es-Semara">Es-Semara</option>
              </select>
              <label  className="field-label">Tél.</label>
              <input type="text" className="text-field-6 w-input"  name="tele" placeholder="Entrer votre téléphone" id="tele" value={formData.tele} onChange={handleChange} required />

              <label htmlFor="email" className="field-label">Email</label>
              <input type="text" className="text-field-6 w-input" name="email" placeholder="Entrer votre adresse email" id="email" value={formData.email} onChange={handleChange} required />

              <label htmlFor="message" className="field-label">Description</label>
              <textarea style={{ resize: 'none', height: '100px' }} className="text-field-6 w-input" name="message" placeholder="Décrivez votre problème" id="message" value={formData.message} onChange={handleChange} required></textarea>

              <div className="div-block-236">
                <button type="button" onClick={sendWhatsApp} id="btnCallMe" style={{ backgroundColor: 'black', color: 'white', height: '40px', fontSize: '13px' }} className="button-20 w-button">
                  <i className='fab fa-whatsapp icon'></i>
                  <h6>Discutez avec moi !</h6>
                </button>
              </div>
            </form>
            {/* <div className="success-message-4 w-form-done" tabIndex="-1" role="region" aria-label="Succès de la demande de rappel">
              <div style={{ color: 'green' }}>Merci, votre demande a bien été envoyée</div>
            </div>
            <div className="error-message-7 w-form-fail" tabIndex="-1" role="region" aria-label="Échec de la demande de rappel">
              <div style={{ color: 'red' }}>Une erreur s'est produite lors de l'envoi de votre demande. Veuillez réessayer plus tard.</div>
            </div> */}
          </div>
          {/* <div id="messageRappel" className="div-block-279">
            <div style={{ color: 'blue' }} className="text-block-177">Votre demande de rappel a bien été envoyée !</div>
          </div> */}
        </div>
      </div>
    </div>
  );
};

export default WhatsAppCall;
