import React, { useState } from 'react';
import { Lock, Person } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import './login.css';
function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const handleCheckboxChange = () => {
    setRememberMe(!rememberMe);
  };
  const sendEmail = (e) => {
    e.preventDefault();
    if (validate()) {
      fetch("http://localhost:3000/login/"+email)
        .then((res)=>{
          if(res.ok) {
            return res.json();
          } else {
            throw new Error('Failed to login');
          }
        })
        .then((resp)=>{
          console.log(resp);
          // Redirect or perform other actions upon successful login
        })
        .catch((err)=>{
          alert('Login failed: '+err.message);
        });
    }
  };
  
  const validate = () => {
    if (!email.trim()) {
      alert('Veuillez entrer votre adresse e-mail.');
      return false;
    }
    
    if (!password.trim()) {
      alert('Veuillez entrer votre mot de passe.');
      return false;
    }
  
    return true;
  };
  

  return (
    <div className='loginTop'>
      <form action='' className='loginForm' onSubmit={sendEmail}>
        <h1>Connectez-vous</h1>
        <div className='inputBox'>
          <input type='email' placeholder='Nom d’utilisateur' value={email} onChange={(e) => setEmail(e.target.value)} required />
          <Person className='iconLogin' />
        </div>
        <div className='inputBox'>
          <input type='password' placeholder='Mot de passe' value={password} onChange={(e) => setPassword(e.target.value)} required />
          <Lock className='iconLogin' />
        </div>
        <div className="rememberForgot">
          <label className='labelLogin'><input className='checkbox' type="checkbox" checked={rememberMe} onChange={handleCheckboxChange} />Rappelle-moi</label>
          <Link to={'/forgotPass'}>Mot de passe oublié?</Link>
        </div>
        <button type='submit'>Login</button>
        <div className='registerLink'>
          <p> Vous n’avez pas de compte? <Link to={'/register'}>Register</Link></p>
        </div>
      </form>
    </div>
  )
}

export default Login;
