import React, { useState } from 'react'
import { Lock, Person } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import './login.css'
function Login() {
    const [nomutilisateur,setNomutilisateur]=useState("");
    const [password,setPassword]=useState(" ");
    const [isChecked, setIsChecked] = useState(false);

    const handleCheckboxChange = (e) => {
      setIsChecked(e.target.checked);
    };
    function registerHandle() {
        console.table(nomutilisateur,password)
        dispatch(signUpUser({nomutilisateur,password}))
    }

    const sendEmail = (e) => {
        e.preventDefault();
    };
  return (
    <div className='loginTop '>
            <form action='' className='loginForm'  onSubmit={sendEmail}>
                <h1>Connectez-vous</h1>
                <div className='inputBox'>
                    <input type='text' placeholder='Nom d’utilisateur' required  value={nomutilisateur} onChange={(e)=>setNomutilisateur(e.target.value)}/>
                    <Person className='iconLogin' />
                </div>
                <div className='inputBox'>
                    <input type='password' placeholder='Mot de passe' required  value={password} onChange={(e)=>setPassword(e.target.value)}/>
                    <Lock className='iconLogin' />
                </div>
                <div className="rememberForgot">
                <label className='labelLogin'><input className='checkbox' type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />Rappelle-moi</label>
                    <Link to={'/forgotPass'}>Mot de passe oublié?</Link>
                </div>
                <button type='submit' onClick={registerHandle}>Login</button>
                <div className='registerLink'>
                    <p> Vous n’avez pas de compte? <Link to={'/register'}>Register</Link></p>
                </div>
            </form>

        </div>
  )
}

export default Login
