import { createSlice } from "@reduxjs/toolkit";
const initState ={articles:[{id:10,nom:"art1",prix:"120",Quantite:'30'},{id:11,nom:"art2",prix:"220",Quantite:'10'}]}

export const userSlice = createSlice({
    name: "user",
    name:"articles",
    initialState: {
        user: null
    },
    reducers: {
        login: (state, action) => {
            state.user = action.payload;
        },
        logout: (state) => {
            state.user = null;
        },
        action1(state, action) {

        },
        incrementer: (state, action) => {
            state.climatisations = state.climatisations.map(cli =>
                cli.id === action.payload.id ? { ...cli, climatisation: parseInt(cli.climatisation) + 1 } : cli
            );
        },
        decrementer: (state, action) => {
            state.climatisations = state.climatisattions.map(cli =>
                cli.id === action.payload.id && parseInt(cli.climatisation) > 0 ? { ...cli, climatisation: parseInt(cli.climatisation) - 1 } : cli
            );
        }

    },
});

export const { login, logout,incrementer,decrementer } = userSlice.actions;
export const selectUser = (state) => state.user.user;
export default userSlice.reducer;
