// pages/inscription/Inscription.js
import React from 'react';
import { connect } from 'react-redux';
import {
  updateEmail,
  updatePassword,
  updateConfirmPassword,
  submitRegistration,
} from '../../redux/action';

const Inscription = ({ email, password, confirmPassword, updateEmail, updatePassword, updateConfirmPassword, submitRegistration }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Dispatch l'action de soumission d'inscription
    submitRegistration();
  };

  return (
    <div>
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        {/* Champ Email */}
        <input type="email" value={email} onChange={(e) => updateEmail(e.target.value)} required />
        {/* Champ Mot de passe */}
        <input type="password" value={password} onChange={(e) => updatePassword(e.target.value)} required />
        {/* Champ Confirmer le mot de passe */}
        <input type="password" value={confirmPassword} onChange={(e) => updateConfirmPassword(e.target.value)} required />
        {/* Bouton de soumission */}
        <button type="submit">S'inscrire</button>
      </form>
    </div>
  );
};

// Mapping de l'état Redux aux props du composant
const mapStateToProps = (state) => ({
  email: state.registration.email,
  password: state.registration.password,
  confirmPassword: state.registration.confirmPassword,
});

// Mapping des actions Redux aux props du composant
const mapDispatchToProps = {
  updateEmail,
  updatePassword,
  updateConfirmPassword,
  submitRegistration,
};

export default connect(mapStateToProps, mapDispatchToProps)(Inscription);
